import * as THREE from "three";
import { drawSceneList } from "../App.vue";
import { index } from './DrawingInput.vue';

class HistoryController {
    constructor() {
        this.undoHistory = [];
        this.redoHistory = [];
    }

    canUndo() {
        return this.undoHistory.length > 0;
    }

    canRedo() {
        return this.redoHistory.length > 0;
    }

    undo() {
        if (!this.canUndo()) {
            return;
        }

        let item = this.undoHistory.pop();
        let action = item[0];
        let object = item[1];

        // Reverse the action
        if (action === 'add') {
            this.remove(object);
            this.redoHistory.push(['remove', object]);
        } else if (action === 'remove') {
            this.add(object);
            this.redoHistory.push(['add', object]);
        }
    }

    redo() {
        if (!this.canRedo()) {
            return;
        }

        let item = this.redoHistory.pop();
        let action = item[0];
        let object = item[1];

        // Reverse the action
        if (action === 'add') {
            this.remove(object);
            this.undoHistory.push(['remove', object]);
        } else if (action === 'remove') {
            drawSceneList[index].add(object);
            this.undoHistory.push(['add', object]);
        }
    }

    action(item){
        this.undoHistory.push(item);
    }

    add(object) {
        drawSceneList[index].add(object);
    }
    remove(object) {
        drawSceneList[index].remove(object);
    }
}

export { HistoryController }
