import * as THREE from "three";
import { MeshLine, MeshLineMaterial } from "three.meshline";
import { scene, renderer, camera } from "../App.vue";

let draw = {
    l: undefined,
    draw: class {
        constructor(stroke) {
            this.stroke = stroke;
            this.line = new MeshLine();
            this.geometry = new THREE.BufferGeometry();
            this.vertices = [];
            this.geometry.setAttribute("position", new THREE.BufferAttribute(new Float32Array(this.vertices), 3));
            this.material = new MeshLineMaterial({
                lineWidth: this.stroke.show_stroke ? this.stroke.lineWidth : 0.005,
                sizeAttenuation: 1,
                color: this.stroke.show_stroke ? this.stroke.color : 0xFFFFFF,
                side: THREE.DoubleSide,
                fog: true,
                wireframe: false,
                alphaTest: 0.9,
                blending: THREE.NormalBlending,
                transparent: false,
                repeat: new THREE.Vector2(1, 1),
                opacity: 1,
            });
            this.mesh = new THREE.Line(this.line.geometry, this.material);
        }

        start() {
            scene.add(this.mesh);
        }

        move(x, y, z) {
            var v3 = new THREE.Vector3(x, y, z);
            this.vertices = [...this.vertices, v3.x, v3.y, v3.z];
            this.geometry.attributes.position.setArray(new Float32Array(this.vertices));
            this.geometry.attributes.position.count = this.vertices.length / 3;
            this.geometry.attributes.position.needsUpdate = true;
            this.line.setGeometry(this.geometry);
        }

        end() {
            this.geometry.computeBoundingSphere();
            renderer.render(scene, camera);
        }
    },
    onStart: function (x, y, z, stroke) {
        this.l = new this.draw(stroke);
        this.l.start();
    },
    onMove: function (x, y, z) {
        this.l.move(x, y, z);
    },
    onEnd: function () {
        this.l.end();
    },
}

export { draw }
