import * as THREE from 'three';

function InfiniteGridHelper(size = 10, divisions = 100, color1 = new THREE.Color('white'), color2 = new THREE.Color('black')) {
    const group = new THREE.Group();

    // Create base grid
    const baseGrid = new THREE.GridHelper(size, divisions, color1, color2);
    group.add(baseGrid);

    // Create thicker lines for every fifth line
    const thickLineSize = size / divisions * 5;
    for(let i = -size / 2; i <= size / 2; i += thickLineSize) {
        const lineGeometry = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(-size / 2, 0, i),
            new THREE.Vector3(size / 2, 0, i),
            new THREE.Vector3(i, 0, -size / 2),
            new THREE.Vector3(i, 0, size / 2)
        ]);

        const lineMaterial = new THREE.LineBasicMaterial({ color: color2, linewidth: 2 });
        const line = new THREE.LineSegments(lineGeometry, lineMaterial);
        group.add(line);
    }

    return group;
}

export default InfiniteGridHelper;
