import * as THREE from 'three';
import { ArcballControls } from 'three/addons/controls/ArcballControls.js';
export let renderer = new THREE.WebGLRenderer({
  alpha: true,
  antialias: true
});
export let camera = new THREE.PerspectiveCamera(45, 1, 1, 10000);
let controls, scene;
export default {
  name: 'ArcballControl',
  props: {
    cameraPosition: {
      type: Object,
      required: true
    },
    cameraAngle: {
      type: Object,
      required: true
    }
  },
  /*
  data() {
    return {
      renderer: renderer,
      controls: null,
      scene: null
    };
  },
  */
  mounted() {
    renderer.setSize(150, 150);
    this.$el.appendChild(renderer.domElement);
    scene = new THREE.Scene();

    //this.$emit('update:camera', camera);

    //this.$emit('update:camera', camera.clone());

    controls = new ArcballControls(camera, renderer.domElement, scene);
    controls.addEventListener('change', this.handleCameraChange);
    camera.position.set(0, 0, 2);
    this.windowResizeHandler();
    controls.update();
    this.animate();
    window.addEventListener('resize', this.windowResizeHandler);
  },
  beforeUnmount() {
    window.removeEventListener('resize', this.windowResizeHandler);
    controls.removeEventListener('change', this.handleCameraChange);
  },
  methods: {
    handleCameraChange() {
      const {
        clientX,
        clientY
      } = controls.getPointerPosition(); // Assuming 'getPointerPosition' is a method that returns the current pointer position.

      // Define the bounds of the Arcball controller.
      const arcballBounds = {
        x: 0,
        y: 0,
        width: 100,
        height: 100
      }; // Update these values to match your actual Arcball position and size.

      // Check if the pointer is within the bounds of the Arcball controller.
      if (clientX >= arcballBounds.x && clientX <= arcballBounds.x + arcballBounds.width && clientY >= arcballBounds.y && clientY <= arcballBounds.y + arcballBounds.height) {
        const cameraPosition = camera.position.clone();
        const cameraAngle = camera.rotation.clone();
        this.$emit('camera-updated', {
          position: cameraPosition,
          angle: cameraAngle
        });
        renderer.render(scene, camera);
      }
    },
    windowResizeHandler() {
      const width = this.$el.clientWidth;
      const height = this.$el.clientHeight;

      /*
      this.$emit('update:camera', {
        aspect: width / height,
        width: width,
        height: height
      });
      */

      renderer.setSize(width, height);
    },
    animate() {
      requestAnimationFrame(this.animate);
      controls.update();
      renderer.render(scene, camera);
    }
  }
};
