import * as THREE from 'three';
const scene = new THREE.Scene();

// Create a renderer
const renderer = new THREE.WebGLRenderer();
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

// Create a camera
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
camera.position.z = 2;
camera.position.x = 0;
renderer.setClearColor(new THREE.Color(0x000000));

// Create a geometry
const geometry = new THREE.BoxGeometry(1, 1, 1);
const edges = new THREE.EdgesGeometry(geometry);

// Create a material

const lineMaterial = new THREE.LineBasicMaterial({
  color: 0x00ff00
});

// Create a mesh

const cube = new THREE.LineSegments(edges, lineMaterial);

// Add the mesh to the scene

scene.add(cube);
const axes = 'xzy';
const planeAxes = axes.slice(0, 2);
const color = new THREE.Color('white');
const size1 = 10;
const size2 = 100;
const distance = 8000;

// Vertex Shader
const vertexShader = `

           varying vec3 worldPosition;

           uniform float uDistance;

           void main() {

                vec3 pos = position.${axes} * uDistance;
                pos.${planeAxes} += cameraPosition.${planeAxes};

                worldPosition = pos;

                gl_Position = projectionMatrix * modelViewMatrix * vec4(pos, 1.0);

           }
           `;
const fragmentShader = `

           varying vec3 worldPosition;

           uniform float uSize1;
           uniform float uSize2;
           uniform vec3 uColor;
           uniform float uDistance;



            float getGrid(float size) {

                vec2 r = worldPosition.${planeAxes} / size;


                vec2 grid = abs(fract(r - 0.5) - 0.5) / fwidth(r);
                float line = min(grid.x, grid.y);


                return 1.0 - min(line, 1.0);
            }

           void main() {


                  float d = 1.0 - min(distance(cameraPosition.${planeAxes}, worldPosition.${planeAxes}) / uDistance, 1.0);

                  float g1 = getGrid(uSize1);
                  float g2 = getGrid(uSize2);


                  gl_FragColor = vec4(uColor.rgb, mix(g2, g1, g1) * pow(d, 3.0));
                  gl_FragColor.a = mix(0.5 * gl_FragColor.a, gl_FragColor.a, g2);

                  if ( gl_FragColor.a <= 0.0 ) discard;


           }

           `;

// Shader Material
const shaderMaterial = new THREE.ShaderMaterial({
  side: THREE.DoubleSide,
  uniforms: {
    scale: {
      value: 3.0
    },
    // Modify this to adjust the grid scale
    uSize1: {
      value: size1
    },
    uSize2: {
      value: size2
    },
    uColor: {
      value: color
    },
    uDistance: {
      value: distance
    }
  },
  transparent: true,
  vertexShader: vertexShader,
  fragmentShader: fragmentShader
});
const planeGeometry = new THREE.PlaneGeometry(100, 100, 10, 10);
const grid = new THREE.Mesh(planeGeometry, shaderMaterial);
scene.add(grid);
let rotationSpeed = 0.01; // Initial rotation speed

// Create a button
const button = document.createElement('button');
button.textContent = 'Increase Speed';
document.body.appendChild(button);

// Add event listener to the button
button.addEventListener('click', () => {
  rotationSpeed += 0.01; // Increase the rotation speed by 0.01
});

function animate() {
  requestAnimationFrame(animate);

  // Rotate the cube
  cube.rotation.x += rotationSpeed;
  cube.rotation.y += rotationSpeed;
  grid.rotation.x += .01;
  grid.rotation.y += .02;

  // Render the scene with the camera
  renderer.render(scene, camera);
}
